-- BOM (per platform)
CREATE TABLE IF NOT EXISTS bom (
  platform_id VARCHAR(10),
  project_id VARCHAR(20),
  product_id VARCHAR(20),
  selected_alt_id VARCHAR(30),
  qty INT,
  tag VARCHAR(50),
  PRIMARY KEY(platform_id, project_id, product_id),
  FOREIGN KEY(project_id) REFERENCES project(project_id),
  FOREIGN KEY(product_id) REFERENCES product(product_id),
  FOREIGN KEY(platform_id, project_id) REFERENCES platform(platform_id, project_id),
  CHECK (qty > 0),
  INDEX idx_bom_project (project_id),
  INDEX idx_bom_product (product_id)
) ENGINE=InnoDB;

