-- Quotation Revision Header (v22 — BOM snapshots)
CREATE TABLE IF NOT EXISTS quotation_rev (
  rev_id VARCHAR(20) PRIMARY KEY,
  project_id VARCHAR(20),
  rev_date DATETIME,
  label VARCHAR(100),
  status ENUM('DRAFT','SUBMITTED','APPROVED') DEFAULT 'DRAFT',
  total_usd DECIMAL(15,2),
  total_thb DECIMAL(15,2),
  margin_xr DECIMAL(6,2),
  margin_ng DECIMAL(5,2),
  margin_av DECIMAL(5,2),
  margin_inco VARCHAR(10),
  notes TEXT,
  FOREIGN KEY(project_id) REFERENCES project(project_id)
) ENGINE=InnoDB;

-- Quotation Revision Line Items
CREATE TABLE IF NOT EXISTS quotation_rev_item (
  rev_id VARCHAR(20),
  platform_id VARCHAR(10),
  product_id VARCHAR(20),
  selected_alt_id VARCHAR(30),
  brand VARCHAR(100),
  model VARCHAR(200),
  qty INT,
  unit_price_usd DECIMAL(12,2),
  amount_usd DECIMAL(15,2),
  tag VARCHAR(50),
  PRIMARY KEY(rev_id, platform_id, product_id),
  FOREIGN KEY(rev_id) REFERENCES quotation_rev(rev_id)
) ENGINE=InnoDB;


