-- Useful Views
CREATE OR REPLACE VIEW v_bom_detail AS
SELECT b.platform_id, p.scope, b.product_id, pr.name as product_name, pr.category,
  pa.brand, pa.model, b.qty, pr.unit, pa.unit_price_usd, b.tag
FROM bom b
JOIN platform p ON b.platform_id=p.platform_id AND b.project_id=p.project_id
JOIN product pr ON b.product_id=pr.product_id
JOIN product_alt pa ON b.product_id=pa.product_id AND b.selected_alt_id=pa.alt_id;

CREATE OR REPLACE VIEW v_quotation_summary AS
SELECT q.quotation_id, s.name as supplier, q.quote_date, q.grand_total,
  COUNT(qi.id) as line_items
FROM quotation q
JOIN supplier s ON q.supplier_id=s.supplier_id
JOIN quotation_item qi ON q.quotation_id=qi.quotation_id
GROUP BY q.quotation_id;

CREATE OR REPLACE VIEW v_quotation_rev_summary AS
SELECT qr.rev_id, qr.label, qr.status, qr.rev_date, qr.total_usd, qr.total_thb,
  qr.margin_xr, qr.margin_ng, qr.margin_av,
  COUNT(qri.product_id) as line_items
FROM quotation_rev qr
LEFT JOIN quotation_rev_item qri ON qr.rev_id=qri.rev_id
GROUP BY qr.rev_id;


