-- ════════════════════════════════════════════════════
-- TABLE 1: document_registry
-- Master list of all bid deliverables (VDRS + Appendices)
-- ════════════════════════════════════════════════════
CREATE TABLE IF NOT EXISTS document_registry (
  doc_id       VARCHAR(10) PRIMARY KEY COMMENT 'D01, D02, ... unique ID',
  vdrs_code    VARCHAR(10) COMMENT 'VDRS code e.g. A01, B01, App-A',
  doc_number   VARCHAR(80) COMMENT 'TNS document number e.g. B1701-7-GEN-TE-BO-0019',
  title        VARCHAR(200) NOT NULL,
  title_th     VARCHAR(200),
  current_rev  VARCHAR(10) DEFAULT 'A0' COMMENT 'Current revision (A0=Rev0, A1=Rev01...)',
  rev_date     DATE,
  status       VARCHAR(20) DEFAULT 'TODO' COMMENT 'TODO|DRAFT|SUBMITTED|APPROVED|SUPERSEDED',
  doc_format   VARCHAR(10) COMMENT 'PDF|XLSX|DOCX|DWG',
  rfq_package  VARCHAR(20) DEFAULT 'BOTH' COMMENT 'RFQ-0019|RFQ-0037|BOTH',
  with_bid     BOOLEAN DEFAULT FALSE COMMENT 'TRUE = must submit with bid',
  native_file  VARCHAR(200) COMMENT 'Latest file path/name',
  effort       VARCHAR(5) COMMENT 'S|M|L|XL',
  responsible  VARCHAR(10) DEFAULT 'SN' COMMENT 'SN=Jack, SD=Sakda, ST=Sutthirak',
  notes        TEXT
);

-- ════════════════════════════════════════════════════
-- TABLE 2: document_revision
-- Tracks every revision of every document
-- ════════════════════════════════════════════════════
CREATE TABLE IF NOT EXISTS document_revision (
  rev_id       INT AUTO_INCREMENT PRIMARY KEY,
  doc_id       VARCHAR(10) NOT NULL,
  rev          VARCHAR(10) NOT NULL COMMENT 'A0, A1, B0...',
  rev_date     DATE NOT NULL,
  author       VARCHAR(10) NOT NULL COMMENT 'SN, SD, ST',
  submitted_to VARCHAR(20) COMMENT 'TNS, CPOC, internal',
  submit_date  DATE,
  changes      TEXT NOT NULL COMMENT 'What changed in this revision',
  impacted_by  TEXT COMMENT 'Root cause: product change, spec change, etc.',
  FOREIGN KEY (doc_id) REFERENCES document_registry(doc_id)
);

-- ════════════════════════════════════════════════════
-- TABLE 3: document_impact_map
-- Links product changes → impacted documents
-- When product X changes → query this table → get list of docs to revise
-- ════════════════════════════════════════════════════
CREATE TABLE IF NOT EXISTS document_impact_map (
  product_id   VARCHAR(20) NOT NULL COMMENT 'Product code from product table, or service item ref (e.g. ITEM8_FAT)',
  doc_id       VARCHAR(10) NOT NULL,
  impact_type  VARCHAR(50) NOT NULL COMMENT 'qty|spec|price|dimension|cert|spare|tool|procedure',
  impact_desc  VARCHAR(200) COMMENT 'What specifically changes in this doc',
  PRIMARY KEY (product_id, doc_id, impact_type),
  FOREIGN KEY (doc_id) REFERENCES document_registry(doc_id),
  INDEX idx_dim_product (product_id)
  -- NOTE: FK to product(product_id) intentionally omitted.
  -- product_id may reference service cost items (ITEM8_FAT, ITEM11_SAT etc.)
  -- that exist in service_cost table, not product table.
  -- Application-level validation required.
);

