-- ════════════════════════════════════════════════════
-- TABLE 3: service_cost — Items 8-15 Cost Build-up
-- ════════════════════════════════════════════════════
-- นี่คือตัวหลัก: แต่ละ row = 1 cost line ของ Item 8-15
-- JSX จะ SUM per rfq+item_no → Appendix-A price schedule
--
-- qty_formula: อธิบายที่มาของ qty (traceability)
-- rate_ref: FK → service_rate.rate_id (สำหรับ rate × qty)
-- manday_ref: FK → service_manday.activity_id (สำหรับ man-day qty)
-- amount_usd: computed = qty × rate (ถ้า rate=TBD → amount=0)
--
-- ⚠ status='TBD' lines จะ flag สีแดงใน JSX
-- ════════════════════════════════════════════════════

CREATE TABLE IF NOT EXISTS service_cost (
  id            INT AUTO_INCREMENT PRIMARY KEY,
  rfq           VARCHAR(10) NOT NULL     COMMENT 'RFQ-0019 or RFQ-0037',
  item_no       INT NOT NULL             COMMENT 'RFQ Item 8-15',
  cost_line     VARCHAR(10) NOT NULL     COMMENT 'Line ID within item: 8.1, 8.2, ...',
  description   VARCHAR(200) NOT NULL,
  cost_type     ENUM('MANDAY','MATERIAL','LUMPSUM','PERDIEM','PER_TYPE','SUBBOM_REF','SPIR_REF','TOOLS_REF') NOT NULL,
  qty           DECIMAL(10,2) DEFAULT 0,
  unit          VARCHAR(30) NOT NULL,
  rate_ref      VARCHAR(20)              COMMENT 'FK service_rate.rate_id',
  manday_ref    VARCHAR(10)              COMMENT 'FK service_manday.activity_id (qty source)',
  qty_formula   VARCHAR(300)             COMMENT 'Human-readable qty derivation',
  amount_usd    DECIMAL(12,2) DEFAULT 0  COMMENT 'Calculated: qty × rate × persons',
  amount_thb    DECIMAL(12,2) DEFAULT 0,
  markup_pct    DECIMAL(5,2) DEFAULT 10  COMMENT 'Default svcAv=10% from user_config.margins',
  status        ENUM('CONFIRMED','ESTIMATED','TBD') DEFAULT 'ESTIMATED',
  source_ref    VARCHAR(100)             COMMENT 'Document reference for traceability',
  remarks       VARCHAR(300),
  updated_at    DATETIME DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP,
  INDEX idx_rfq_item (rfq, item_no),
  INDEX idx_status (status)
) ENGINE=InnoDB;


