-- 5. VIEWS — Quick Roll-up Queries
-- ════════════════════════════════════════════════════

-- View: service cost summary per RFQ × Item
CREATE OR REPLACE VIEW v_service_cost_summary AS
SELECT
  rfq,
  item_no,
  CASE item_no
    WHEN 8  THEN 'FAT'
    WHEN 9  THEN 'Document'
    WHEN 10 THEN 'Packing'
    WHEN 11 THEN 'SAT/Offshore'
    WHEN 12 THEN 'Comm. Spares'
    WHEN 13 THEN 'Special Tools'
    WHEN 14 THEN 'Training'
    WHEN 15 THEN 'Import License'
  END AS item_name,
  COUNT(*) AS cost_lines,
  SUM(amount_usd) AS total_usd,
  SUM(amount_thb) AS total_thb,
  SUM(CASE WHEN status='TBD' THEN 1 ELSE 0 END) AS tbd_count,
  SUM(CASE WHEN status='TBD' THEN 1 ELSE 0 END) > 0 AS has_tbd,
  GROUP_CONCAT(DISTINCT rate_ref ORDER BY rate_ref) AS rate_refs
FROM service_cost
GROUP BY rfq, item_no
ORDER BY rfq, item_no;

-- View: Appendix-A price schedule mapping (Items 1-15)
-- Items 1-7: from BOM material cost (existing v_bom_detail)
-- Items 8-15: from service_cost (this patch)
CREATE OR REPLACE VIEW v_appendix_a_cost AS
SELECT
  rfq,
  item_no,
  item_name,
  total_usd AS cost_usd,
  total_thb AS cost_thb,
  ROUND(total_usd * (1 + 10.0/100), 2) AS sell_usd,  -- svcAv=10% markup
  has_tbd,
  tbd_count,
  'service_cost' AS data_source
FROM v_service_cost_summary
UNION ALL
SELECT
  'RFQ-0019' AS rfq,
  0 AS item_no,
  'Items 1-7 MATERIAL (from BOM)' AS item_name,
  COALESCE(SUM(pa.unit_price_usd * b.qty), 0) AS cost_usd,
  0 AS cost_thb,
  0 AS sell_usd,
  FALSE AS has_tbd,
  0 AS tbd_count,
  'bom+product_alt' AS data_source
FROM bom b
JOIN platform p ON b.platform_id=p.platform_id AND b.project_id=p.project_id
JOIN product_alt pa ON b.product_id=pa.product_id AND b.selected_alt_id=pa.alt_id
WHERE p.scope='GF'
ORDER BY rfq, item_no;

-- View: FAT sub-BOM summary
CREATE OR REPLACE VIEW v_fat_subbom_summary AS
SELECT
  category,
  COUNT(*) AS items,
  SUM(amount_usd) AS subtotal_usd,
  SUM(CASE WHEN reusable=1 THEN amount_usd ELSE 0 END) AS reusable_usd,
  SUM(CASE WHEN reusable=0 THEN amount_usd ELSE 0 END) AS consumable_usd
FROM fat_subbom
GROUP BY category
WITH ROLLUP;

-- View: TBD rate checker (for JSX red flag)
CREATE OR REPLACE VIEW v_tbd_rates AS
SELECT
  sc.rfq, sc.item_no, sc.cost_line, sc.description,
  sr.rate_id, sr.description AS rate_desc, sr.rate_usd, sr.status AS rate_status
FROM service_cost sc
LEFT JOIN service_rate sr ON sc.rate_ref = sr.rate_id
WHERE sc.status = 'TBD'
   OR (sr.rate_id IS NOT NULL AND sr.rate_usd = 0)
ORDER BY sc.rfq, sc.item_no, sc.cost_line;


